CREATE TABLE Channels (id int primary key , name char(100), telegram_id int, last_update timestamp);
CREATE TABLE Users (id int primary key , telegram_id int);
CREATE TABLE Subscriptions (user_id int, channel_id int, last_update timestamp);
SELECT Users.id, Users.telegram_id FROM Users JOIN Subscriptions ON Users.id=Subscriptions.user_id JOIN Channels ON Subscriptions.channel_id=Channels.id WHERE Channels.name = 'pi';
SELECT Channels.id, Channels.name, Channels.telegram_id FROM Channels JOIN Subscriptions ON Channels.id=Subscriptions.channel_id JOIN Users ON Subscriptions.user_id=Users.id WHERE Users.telegram_id = 666;
SELECT Channels.id, Channels.name, Channels.telegram_id FROM Channels JOIN Subscriptions ON Channels.id=Subscriptions.channel_id JOIN Users ON Subscriptions.user_id=Users.id WHERE Users.telegram_id = 1488;
DELETE FROM Subscriptions USING Users WHERE Subscriptions.user_id = Users.id AND Users.telegram_id = 666;
DELETE FROM Subscriptions USING Channels, Users WHERE Subscriptions.channel_id = Channels.id AND Channels.name = 'e' AND Subscriptions.user_id = Users.id AND Users.telegram_id = '1488';
